mapper.select:
SELECT ermbs.ID rmbsNumber, ti.NAME trainingName, ind.PESEL pesel, ind.FIRST_NAME participantName, ind.LAST_NAME participantSurname, ermbs.REIMBURSEMENT_DATE rmbsDate, status.NAME rmbsStatus FROM APP_PBE.E_REIMBURSEMENTS ermbs LEFT JOIN APP_PBE.TI_TRAINING_INSTANCES ti_in ON ti_in.ID = ermbs.TI_TR_INST_ID LEFT JOIN APP_PBE.TI_TRAININGS ti ON ti.ID = ti_in.TRAINING_ID LEFT JOIN APP_PBE.INDIVIDUALS ind ON ind.ID = ti_in.INDIVIDUAL_ID LEFT JOIN APP_PBE.E_REIMBURSEMENT_STATUSES status ON status.ID = ermbs.STATUS_ID
ermbs.ID = #{criteria.rmbsNumber}
AND LOWER(ti.NAME) like '%' || LOWER(#{criteria.trainingName}) || '%'
AND ind.PESEL like '%' || #{criteria.pesel} || '%'
AND LOWER(ind.FIRST_NAME) like '%' || LOWER(#{criteria.participantName}) || '%'
AND LOWER(ind.LAST_NAME) like '%' || LOWER(#{criteria.participantSurname}) || '%'
AND TRIM(ermbs.REIMBURSEMENT_DATE) >= TRIM(#{criteria.rmbsDateFrom})
AND TRIM(ermbs.REIMBURSEMENT_DATE) <= TRIM(#{criteria.rmbsDateTo})
AND status.NAME = #{criteria.rmbsStatus}
AND ROWNUM <= #{criteria.limit}
AND ti.TRAINING_INSTITUTION_ID = #{criteria.gryfUser.trainingInstitutionId}
${columnName} ${criteria.sortTypes[index]}
