mapper.select:
SELECT * FROM (SELECT ti_in.ID trainingInstanceId, tri.ID trainingInstitutionId, tri.NAME trainingInstitutionName, tri.VAT_REG_NUM trainingInstitutionVatRegNum, ti.ID trainingId, ti.NAME trainingName, ti.EXTERNAL_ID trainingExternalId, ind.PESEL participantPesel, ind.FIRST_NAME participantName, ind.LAST_NAME participantSurname, ti.START_DATE startDate, ti.END_DATE endDate, status.ID trainingStatusId, status.NAME trainingStatus, gp.id grantProgramId, gp.program_name grantProgramName FROM APP_PBE.TI_TRAINING_INSTANCES ti_in JOIN APP_PBE.TI_TRAININGS ti ON ti.ID = ti_in.TRAINING_ID JOIN APP_PBE.GRANT_PROGRAMS gp ON ti_in.grant_program_id = gp.id JOIN APP_PBE.TRAINING_INSTITUTIONS tri ON tri.ID = ti.TRAINING_INSTITUTION_ID LEFT JOIN APP_PBE.INDIVIDUALS ind ON ind.ID = ti_in.INDIVIDUAL_ID LEFT JOIN APP_PBE.TI_TRAINING_INSTANCE_STATUSES status ON status.ID = ti_in.STATUS_ID
AND ti_in.ID = #{criteria.trainingInstanceId}
AND tri.ID = #{criteria.trainingInstitutionId}
AND tri.VAT_REG_NUM = #{criteria.trainingInstitutionVatRegNum}
AND LOWER(tri.NAME) like '%' || LOWER(#{criteria.trainingInstitutionName}) || '%'
AND ti.ID = #{criteria.trainingId}
AND LOWER(ti.NAME) like '%' || LOWER(#{criteria.trainingName}) || '%'
AND LOWER(ti.EXTERNAL_ID) like '%' || LOWER(#{criteria.trainingExternalId}) || '%'
AND ind.PESEL like '%' || #{criteria.participantPesel} || '%'
AND LOWER(ind.FIRST_NAME) like '%' || LOWER(#{criteria.participantName}) || '%'
AND LOWER(ind.LAST_NAME) like '%' || LOWER(#{criteria.participantSurname}) || '%'
AND TRIM(ti.START_DATE) >= TRIM(#{criteria.startDateFrom})
AND TRIM(ti.START_DATE) <= TRIM(#{criteria.startDateTo})
AND TRIM(ti.END_DATE) >= TRIM(#{criteria.endDateFrom})
AND TRIM(ti.END_DATE) <= TRIM(#{criteria.endDateTo})
AND status.ID = #{criteria.trainingStatusId}
AND ti.TRAINING_INSTITUTION_ID = #{criteria.gryfUser.trainingInstitutionId}
AND LOWER(gp.program_name) like '%' || LOWER(#{criteria.grantProgramName}) || '%'
AND gp.id = #{criteria.grantProgramId}
${columnName} ${criteria.sortTypes[index]}
)
ROWNUM <= #{criteria.limit} + 1

SELECT distinct tin.ID trainingInstanceId, tin.STATUS_ID trainingInstanceStatusId, tin.ASSIGNED_NUM productAssignedNum, tin.VERSION trainingInstanceVersion, CASE tin.OPINION_DONE WHEN 'Y' THEN 1 ELSE 0 END opinionDone, tra.ID trainingId, tra.NAME trainingName, tra.EXTERNAL_ID trainingExternalId, cat.NAME trainingCategory, tra.PLACE trainingPlace, tra.PRICE trainingPrice, tra.HOURS_NUMBER trainingHoursNumber, tra.HOUR_PRICE trainingHourPrice, trin.ID trainingInstitutionId, trin.NAME trainingInstitutionName, trin.VAT_REG_NUM trainingInstitutionVatRegNum, tra.START_DATE startDate, tra.END_DATE endDate, ind.ID participantId, ind.PESEL participantPesel, ind.FIRST_NAME participantName, ind.LAST_NAME participantSurname, tin.PRODUCT_INSTANCE_CALC_FOR_HOUR productInstanceForHour, tr_cat_par.MAX_PRODUCT_INSTANCE maxProductInstance, prd.VALUE prdValue, tin.GRANT_PROGRAM_ID grantProgramId, gr_pr.PROGRAM_NAME grantProgramName, tra.REIMBURSMENT_CONDITIONS reimbursmentConditions FROM APP_PBE.TI_TRAINING_INSTANCES tin JOIN APP_PBE.TI_TRAININGS tra ON tra.ID = tin.TRAINING_ID JOIN APP_PBE.TI_TRAINING_CATEGORIES cat ON cat.ID = tra.TRAINING_CATEGORY_ID JOIN APP_PBE.TI_TRAINING_CATEGORY_PARAMS tr_cat_par ON (cat.ID = tr_cat_par.CATEGORY_ID AND tin.GRANT_PROGRAM_ID = tr_cat_par.GRANT_PROGRAM_ID) JOIN APP_PBE.INDIVIDUALS ind ON ind.ID = tin.INDIVIDUAL_ID JOIN APP_PBE.GRANT_PROGRAMS gr_pr ON gr_pr.ID = tin.GRANT_PROGRAM_ID JOIN APP_PBE.GRANT_OWNERS gr_own ON gr_own.ID = gr_pr.GRANT_OWNER_ID JOIN APP_PBE.PBE_PRODUCTS prd ON prd.GRANT_OWNER_ID = gr_own.ID JOIN APP_PBE.TRAINING_INSTITUTIONS trin on trin.ID = tra.TRAINING_INSTITUTION_ID
tin.ID = #{trainingInstanceId}
AND tra.TRAINING_INSTITUTION_ID = #{criteria.gryfUser.trainingInstitutionId}

SELECT distinct tin.ID trainingInstanceId, tin.STATUS_ID trainingInstanceStatusId, tin.ASSIGNED_NUM productAssignedNum, tin.VERSION trainingInstanceVersion, CASE tin.OPINION_DONE WHEN 'Y' THEN 1 ELSE 0 END opinionDone, tra.ID trainingId, tra.NAME trainingName, tra.EXTERNAL_ID trainingExternalId, cat.NAME trainingCategory, tra.PLACE trainingPlace, tra.PRICE trainingPrice, tra.HOURS_NUMBER trainingHoursNumber, tra.HOUR_PRICE trainingHourPrice, trin.ID trainingInstitutionId, trin.NAME trainingInstitutionName, trin.VAT_REG_NUM trainingInstitutionVatRegNum, tra.START_DATE startDate, tra.END_DATE endDate, ind.ID participantId, ind.PESEL participantPesel, ind.FIRST_NAME participantName, ind.LAST_NAME participantSurname, tin.PRODUCT_INSTANCE_CALC_FOR_HOUR productInstanceForHour, tr_cat_par.MAX_PRODUCT_INSTANCE maxProductInstance, prd.VALUE prdValue, tin.GRANT_PROGRAM_ID grantProgramId, gr_pr.PROGRAM_NAME grantProgramName, tra.REIMBURSMENT_CONDITIONS reimbursmentConditions, tin.REIMBURSMENT_PIN pinCode FROM APP_PBE.TI_TRAINING_INSTANCES tin JOIN APP_PBE.TI_TRAININGS tra ON tra.ID = tin.TRAINING_ID JOIN APP_PBE.TI_TRAINING_CATEGORIES cat ON cat.ID = tra.TRAINING_CATEGORY_ID JOIN APP_PBE.TI_TRAINING_CATEGORY_PARAMS tr_cat_par ON (cat.ID = tr_cat_par.CATEGORY_ID AND tin.GRANT_PROGRAM_ID = tr_cat_par.GRANT_PROGRAM_ID) JOIN APP_PBE.INDIVIDUALS ind ON ind.ID = tin.INDIVIDUAL_ID JOIN APP_PBE.GRANT_PROGRAMS gr_pr ON gr_pr.ID = tin.GRANT_PROGRAM_ID JOIN APP_PBE.GRANT_OWNERS gr_own ON gr_own.ID = gr_pr.GRANT_OWNER_ID JOIN APP_PBE.PBE_PRODUCTS prd ON prd.GRANT_OWNER_ID = gr_own.ID JOIN APP_PBE.TRAINING_INSTITUTIONS trin on trin.ID = tra.TRAINING_INSTITUTION_ID
tin.ID = #{trainingInstanceId}
AND tra.TRAINING_INSTITUTION_ID = #{criteria.gryfUser.trainingInstitutionId}
