mapper.select:
SELECT t.TRA_ID as trainingId, t.TRIN_ID as institutionId, i.NAME as institutionName, t.NAME, t.PRICE, t.START_DATE as startDate, t.END_DATE as endDate, t.PLACE, t.HOURS_NUMBER as hoursNumber, t.HOUR_PRICE as hourPrice, c.description as category
FROM APP_PBE.TI_TRAININGS t
JOIN APP_PBE.TI_TRAINING_CATEGORY_DICS c on c.TRC_CODE = t.TRC_CODE
JOIN APP_PBE.TRAINING_INSTITUTIONS i on i.ID = t.TRIN_ID

    
        t.TRA_ID = #{searchParams.trainingId}
    
    
        AND t.TRIN_ID = #{searchParams.institutionId}
    
    
        AND LOWER(i.NAME) like '%' || LOWER(#{searchParams.institutionName}) || '%'
    
    
        AND LOWER(t.NAME) like '%' || LOWER(#{searchParams.name}) || '%'
    
    
        AND t.PRICE >= #{searchParams.priceFrom}
    
    
        AND t.PRICE <= #{searchParams.priceTo}
    
    
        AND t.START_DATE >= #{searchParams.startDate}
    
    
        AND t.END_DATE <= #{searchParams.endDate}
    
    
        AND LOWER(t.PLACE) like '%' || LOWER(#{searchParams.place}) || '%'
    
    
        AND t.HOURS_NUMBER >= #{searchParams.hoursNumberFrom}
    
    
        AND t.HOURS_NUMBER <= #{searchParams.hoursNumberTo}
    
    
        AND t.HOUR_PRICE >= #{searchParams.hourPriceFrom}
    
    
        AND t.HOUR_PRICE <= #{searchParams.hourPriceTo}
    
    
        AND t.TRC_CODE like #{searchParams.categoryCode}
    


    
        ${columnName} ${searchParams.sortTypes[index]}
